import './App.css';
import React from 'react';
import lottie from 'lottie-web';

import Primeiro from './components/basicos/Primeiro';
import ComParamentro from './components/basicos/ComParamentro';
import ComFilhos from './components/basicos/ComFilhos';
import Card from './components/layout/Card';
import Repeticao from './components/basicos/Repeticao';
import Condicional from './components/basicos/Condicional';
import CondicionalComIf from './components/basicos/CondicionalComIf';
import Pai from './components/comunicacao/direta/Pai';
import { async } from 'q';




  export default async(props) => {

    await lottie.loadAnimation({
        container: document.getElementById('animacao'), // the dom element that will contain the animation
        renderer: 'svg',
        loop: true,
        autoplay: true,
        path: 'https://assets8.lottiefiles.com/packages/lf20_gomzks5q.json' // the path to the animation json
      });

 return(
     <div className="App">

        <Card titulo="animação">
            <div id="animacao">
            </div>
        </Card>

        <Card titulo="#07 - Comunicação Direta " color="#FF4500">
            <Pai></Pai>  
        </Card>

        <Card titulo="#06 - Condicional com If " color="#FA6900">
            <CondicionalComIf numero={5} ></CondicionalComIf>
        </Card>

        <Card titulo="#05 - Condicional v1" color="#E94C6F">
            <Condicional numero={11} />
        </Card>

        <Card titulo="#04 - Componentes de Repetição" color="#0008BBA">
            <Repeticao />
        </Card>

        <Card titulo="#03 - Componentes com filhos" color="#D96459">
            <ComFilhos>
                <ul>
                    <li>Ana</li>
                    <li>Bia</li>
                    <li>Carlos</li>
                    <li>Daniel</li>
                </ul>
            </ComFilhos>
        </Card>

        <Card titulo="#02 - Componente com parametro" color="#FF85CB">
            <ComParamentro titulo="Esse é o Titulo" subtitulo="Esse é o subtitulo" />
        </Card>

        <Card titulo="#01 - Primeiro Componente" color="#B0E0E6">
            <Primeiro />
        </Card>

    </div>
  ); 

}
